func TestCreateTodo(t *testing.T) {
	mockRepository := mocks.NewTodoRepository(t)
	usecase := usecase.NewTodoUsecase(mockRepository)
	createRequest := &domain.CreateTodoRequest{
		ID: "f2fd5cdf-4feb-42bf-9372-6bd1f5dae31f",
		TodoRequest: domain.TodoRequest{
			Title:       "Get Profile API",
			Description: "Create API for get profile data",
			Date:        time.Date(2024, 1, 1, 0, 0, 0, 0, util.LoadLocation()),
			Image:       "",
			Status:      domain.StatusInProgress,
		},
	}

	t.Run("success", func(t *testing.T) {
		mockRepository.On("ReadTodoJSON").Return([]*domain.Todo{}, nil).Once()

		mockRepository.On("WriteTodoJSON", mock.AnythingOfType("[]*domain.Todo")).Return(nil).Once()

		err := usecase.Create(createRequest)
		assert.NoError(t, err)

		mockRepository.AssertExpectations(t)
	})

	t.Run("wrtie todo json error", func(t *testing.T) {
		mockRepository.On("ReadTodoJSON").Return([]*domain.Todo{}, nil).Once()

		mockRepository.On("WriteTodoJSON", mock.AnythingOfType("[]*domain.Todo")).Return(errors.New("write todo json error")).Once()

		err := usecase.Create(createRequest)
		assert.Error(t, err)

		mockRepository.AssertExpectations(t)
	})

	t.Run("read todo json error", func(t *testing.T) {
		mockRepository.On("ReadTodoJSON").Return(nil, errors.New("read todo json error")).Once()

		err := usecase.Create(createRequest)
		assert.Error(t, err)

		mockRepository.AssertExpectations(t)
	})
}

func TestGetOneTodo(t *testing.T) {
	mockRepository := mocks.NewTodoRepository(t)
	usecase := usecase.NewTodoUsecase(mockRepository)
	id := "f2fd5cdf-4feb-42bf-9372-6bd1f5dae31f"
	t.Run("success", func(t *testing.T) {
		mockRepository.On("ReadTodoJSON").Return([]*domain.Todo{{
			ID:          "f2fd5cdf-4feb-42bf-9372-6bd1f5dae31f",
			Title:       "Get Profile API",
			Description: "Create API for get profile data",
			Date:        time.Date(2024, 1, 1, 0, 0, 0, 0, util.LoadLocation()),
			Image:       "",
			Status:      domain.StatusInProgress,
		}}, nil).Once()

		todo, err := usecase.GetOne(id)
		assert.NoError(t, err)
		assert.NotNil(t, todo)

		mockRepository.AssertExpectations(t)
	})

	t.Run("data not found", func(t *testing.T) {
		mockRepository.On("ReadTodoJSON").Return([]*domain.Todo{}, nil).Once()

		todo, err := usecase.GetOne(id)
		assert.Error(t, err)
		assert.Nil(t, todo)

		mockRepository.AssertExpectations(t)
	})

	t.Run("read to do json error", func(t *testing.T) {
		mockRepository.On("ReadTodoJSON").Return(nil, errors.New("read todo json error")).Once()

		todo, err := usecase.GetOne(id)
		assert.Error(t, err)
		assert.Nil(t, todo)

		mockRepository.AssertExpectations(t)
	})

}

